def _get_category_delivery_settings(cat_name):
    """ì¹´í…Œê³ ë¦¬ë³„ ë°°ì†¡ë£Œ ì„¤ì • ë°˜í™˜. (base_fee, free_over, extra_threshold, extra_fee, fee_per_item)."""
    cat = Category.query.filter_by(name=cat_name).first()
    if not cat:
        return 1900, None, 50000, 1900, 0
    base = int(getattr(cat, 'delivery_base_fee', None) or 1900)
    free_over = getattr(cat, 'delivery_free_over', None)
    if free_over is not None:
        free_over = int(free_over)
    extra_threshold = getattr(cat, 'delivery_extra_threshold', None)
    if extra_threshold is None:
        extra_threshold = 50000
    else:
        extra_threshold = int(extra_threshold)
    extra_fee = int(getattr(cat, 'delivery_extra_fee', None) or 1900)
    per_item = int(getattr(cat, 'delivery_fee_per_item', None) or 0)
    return base, free_over, extra_threshold, extra_fee, per_item


def calc_delivery_fee_for_categories(cat_price_sums, cat_item_counts=None):
    """ì¹´í…Œê³ ë¦¬ë³„ ê¸ˆì•¡/ê±´ë³„ ë°°ì†¡ë£Œ ê³„ì‚°. Returns (total_fee, breakdown_dict)."""
    if not cat_price_sums:
        return 0, {}
    cat_item_counts = cat_item_counts or {}
    total = 0
    breakdown = {}
    for cat_name, amt in cat_price_sums.items():
        base, free_over, extra_threshold, extra_fee, per_item = _get_category_delivery_settings(cat_name)
        cnt = cat_item_counts.get(cat_name, 0)
        if free_over is not None and amt >= free_over:
            cat_fee = 0 + per_item * cnt
        else:
            add_extra = (extra_fee if (extra_threshold and amt >= extra_threshold) else 0)
            cat_fee = base + add_extra + per_item * cnt
        total += cat_fee
        breakdown[cat_name] = cat_fee
    return total, breakdown


def get_category_delivery_description(cat_name):
    """í•œ ì¹´í…Œê³ ë¦¬ì˜ ë°°ì†¡ë£Œ ì•ˆë‚´ ë¬¸êµ¬ (ìƒí’ˆìƒì„¸Â·ì¹´í…Œê³ ë¦¬ í˜ì´ì§€ìš©)."""
    base, free_over, extra_threshold, extra_fee, per_item = _get_category_delivery_settings(cat_name)
    parts = []
    if free_over is not None:
        parts.append(f"{free_over:,}ì› ì´ìƒ êµ¬ë§¤ ì‹œ ë¬´ë£Œ")
    if base and (free_over is None or free_over > 0):
        parts.append(f"ê¸°ë³¸ {base:,}ì›")
    if extra_threshold and extra_fee:
        parts.append(f"{extra_threshold:,}ì› ì´ìƒ ì‹œ +{extra_fee:,}ì›")
    if per_item:
        parts.append(f"ê±´ë‹¹ +{per_item:,}ì›")
    if not parts:
        return "ë°°ì†¡ë£Œ ë¬¸ì˜"
    return " Â· ".join(parts)


def is_address_in_delivery_zone(address_str):
    """ì£¼ì†Œê°€ ë°°ì†¡ ê°€ëŠ¥í•œì§€ (ê¸°ë³¸ ê¶Œì—­ì´ë“  í€µ ê¶Œì—­ì´ë“  í¬í•¨). use_quick_region_only ì‹œ í€µê¶Œì—­ë§Œ ë°°ì†¡ê°€ëŠ¥. ë§ˆì´í˜ì´ì§€Â·ê²°ì œ ë™ì¼ ì •ê·œí™” ì ìš©."""
    zone = _get_zone()
    addr = _normalize_address_for_zone(address_str)
    if not addr:
        return False
    use_quick_only = bool(getattr(zone, 'use_quick_region_only', False)) if zone else False
    if use_quick_only:
        if is_address_in_quick_polygon(addr):
            return True
        quick = _get_quick_region_list(zone)
        return bool(quick and any(name in addr for name in quick))
    if is_address_in_main_polygon(addr):
        return True
    if is_address_in_quick_polygon(addr):
        return True
    quick = _get_quick_region_list(zone) if zone else []
    if quick:
        return any(name in addr for name in quick)
    # ë°°ì†¡ê¶Œì—­ ì´ë¦„(ì˜ˆ: ì—°ìˆ˜êµ¬)ì´ ì£¼ì†Œì— ìˆìœ¼ë©´ ë°°ì†¡ê°€ëŠ¥ìœ¼ë¡œ ì²˜ë¦¬ (ì§€ì˜¤ì½”ë”©/í´ë¦¬ê³¤ ì˜¤ì°¨ ë³´ì •)
    if zone and getattr(zone, 'name', None) and (zone.name or '').strip():
        if (zone.name or '').strip() in addr:
            return True
    if zone and zone.polygon_json:
        try:
            polygon = json.loads(zone.polygon_json)
            if polygon and len(polygon) >= 3:
                coords = _geocode_address(addr)
                if coords:
                    lat, lng = coords[0], coords[1]
                    px, py = _polygon_point_as_xy(polygon, lat, lng)
                    if _point_in_polygon(px, py, polygon):
                        return True
                    # í´ë¦¬ê³¤ì´ [lng,lat]ì¸ë° [lat,lng]ë¡œ ì¶”ì •ëœ ê²½ìš° ë“± ì¢Œí‘œ ìˆœì„œ ë°˜ëŒ€ ì‹œ í•œ ë²ˆ ë” ì‹œë„
                    if _point_in_polygon(py, px, polygon):
                        return True
        except Exception:
            pass
    return False


def _get_user_total_paid(user_id):
    """íšŒì› ëˆ„ì  ê²°ì œ ê¸°ì¤€ ì‹¤ì œ ê²°ì œê¸ˆì•¡: ë°°ì†¡ì™„ë£Œëœ í’ˆëª© ê¸ˆì•¡ë§Œ ì§‘ê³„ (ì·¨ì†ŒÂ·í™˜ë¶ˆ ì£¼ë¬¸ ì œì™¸)."""
    total = db.session.query(
        func.coalesce(func.sum(OrderItem.price * OrderItem.quantity), 0)
    ).join(Order, OrderItem.order_id == Order.id).filter(
        Order.user_id == user_id,
        Order.status.notin_(['ì·¨ì†Œ', 'í™˜ë¶ˆ']),
        OrderItem.cancelled == False,
        OrderItem.item_status == 'ë°°ì†¡ì™„ë£Œ'
    ).scalar()
    return int(total) if total is not None else 0


def _get_member_grade_config():
    """íšŒì› ë“±ê¸‰ ê¸°ì¤€ ê¸ˆì•¡ ë°˜í™˜. (min_grade2, min_grade3, min_grade4, min_grade5) ìˆœì„œ."""
    def get_val(k, default=0):
        row = MemberGradeConfig.query.filter_by(key=k).first()
        if not row or not row.value:
            return default
        try:
            return int(row.value)
        except (ValueError, TypeError):
            return default
    return (
        get_val('min_amount_grade2', 100000),
        get_val('min_amount_grade3', 500000),
        get_val('min_amount_grade4', 1000000),
        get_val('min_amount_grade5', 2000000),
    )


def recompute_member_grade_for_user(user):
    """ê²°ì œ ëˆ„ì  ê¸ˆì•¡ ê¸°ì¤€ìœ¼ë¡œ ë“±ê¸‰ ì¬ê³„ì‚°. member_grade_overridden ì´ë©´ ë³€ê²½í•˜ì§€ ì•ŠìŒ. ë°˜í™˜: ë³€ê²½ ì—¬ë¶€."""
    if getattr(user, 'member_grade_overridden', False):
        return False
    total = _get_user_total_paid(user.id)
    min2, min3, min4, min5 = _get_member_grade_config()
    new_grade = 1
    if total >= min5:
        new_grade = 5
    elif total >= min4:
        new_grade = 4
    elif total >= min3:
        new_grade = 3
    elif total >= min2:
        new_grade = 2
    if user.member_grade != new_grade:
        user.member_grade = new_grade
        return True
    return False


def categories_for_member_grade(member_grade):
    """í•´ë‹¹ ë“±ê¸‰ íšŒì›ì´ ì´ìš© ê°€ëŠ¥í•œ ì¹´í…Œê³ ë¦¬ ëª©ë¡. member_gradeëŠ” 1~5, ë„ì´ë©´ 1ë¡œ ê°„ì£¼."""
    grade = 1 if member_grade is None else max(1, min(5, member_grade))
    return Category.query.filter(
        or_(Category.min_member_grade.is_(None), Category.min_member_grade <= grade)
    ).order_by(Category.order.asc(), Category.id.asc())


def _get_point_config():
    """í¬ì¸íŠ¸ ì •ì±… ë°˜í™˜. accumulation_rate: 1=0.1%, min_order_to_use(ì›), max_points_per_order(ì›)"""
    def get_val(k, default=0):
        row = PointConfig.query.filter_by(key=k).first()
        if not row or not row.value:
            return default
        try:
            return int(row.value)
        except (ValueError, TypeError):
            return default
    return (
        get_val('accumulation_rate', 1),
        get_val('min_order_to_use', 30000),
        get_val('max_points_per_order', 3000),
    )


def _get_point_config_val(key, default=0):
    """ë‹¨ì¼ í¬ì¸íŠ¸ ì„¤ì •ê°’ ì •ìˆ˜ ë°˜í™˜."""
    row = PointConfig.query.filter_by(key=key).first()
    if not row or row.value is None or row.value == '':
        return default
    try:
        return int(row.value)
    except (ValueError, TypeError):
        return default


def _get_point_policy(ptype, grade):
    """ìœ í˜•Â·ë“±ê¸‰ë³„ í¬ì¸íŠ¸ ì‚¬ìš© ì •ì±…. ptype: accumulated/event/cash, grade: 1~5.
    ë¨¼ì € {ptype}_grade_{grade}_* ì¡°íšŒ, 0ì´ë©´ ìœ í˜• ê¸°ë³¸ê°’Â·ì „ì—­ê°’ ì‚¬ìš©."""
    rate, global_min_order, global_max_order = _get_point_config()
    g = max(1, min(5, int(grade) if grade is not None else 1))
    min_order = _get_point_config_val(f'{ptype}_grade_{g}_min_order', 0)
    valid_days = _get_point_config_val(f'{ptype}_grade_{g}_valid_days', 0)
    max_count = _get_point_config_val(f'{ptype}_grade_{g}_max_use_count', 0)
    max_per_order = _get_point_config_val(f'{ptype}_grade_{g}_max_use_per_order', 0)
    if min_order <= 0:
        min_order = _get_point_config_val(f'{ptype}_min_order_amount', 0)
    if min_order <= 0:
        min_order = global_min_order
    if max_per_order <= 0:
        max_per_order = _get_point_config_val(f'{ptype}_max_use_per_order', 0)
    if max_per_order <= 0:
        max_per_order = global_max_order
    if valid_days <= 0:
        valid_days = _get_point_config_val(f'{ptype}_valid_days', 0)
    if max_count <= 0:
        max_count = _get_point_config_val(f'{ptype}_max_use_count', 0)
    return {
        'min_order_amount': min_order,
        'valid_days': valid_days,
        'max_use_count': max_count,
        'max_use_per_order': max_per_order,
    }


def _point_use_count_in_days(user_id, ptype, days):
    """í•´ë‹¹ ìœ í˜• í¬ì¸íŠ¸ë¥¼ ìµœê·¼ daysì¼ ë‚´ ì‚¬ìš©í•œ íšŸìˆ˜(ì£¼ë¬¸ ê¸°ì¤€)."""
    if not user_id or days <= 0:
        return 0
    since = datetime.now() - timedelta(days=days)
    return PointLog.query.filter(
        PointLog.user_id == user_id,
        PointLog.point_type == ptype,
        PointLog.amount < 0,
        PointLog.created_at >= since,
    ).count()


def _effective_max_point_use(user, order_total):
    """íšŒì›ì´ ì´ ì£¼ë¬¸ì—ì„œ ì‚¬ìš© ê°€ëŠ¥í•œ ìµœëŒ€ í¬ì¸íŠ¸(ì›). ë ˆë²¨ë³„Â·ìœ í˜•ë³„ ì •ì±… ì ìš©."""
    if not user or order_total is None:
        return 0
    grade = max(1, min(5, getattr(user, 'member_grade', 1) or 1))
    available = 0
    for ptype, col in [
        (POINT_TYPE_ACCUMULATED, 'points_accumulated'),
        (POINT_TYPE_EVENT, 'points_event'),
        (POINT_TYPE_CASH, 'points_cash'),
    ]:
        balance = getattr(user, col, 0) or 0
        if balance <= 0:
            continue
        policy = _get_point_policy(ptype, grade)
        if order_total < policy['min_order_amount']:
            continue
        if policy['valid_days'] > 0 and policy['max_use_count'] > 0:
            used_count = _point_use_count_in_days(user.id, ptype, policy['valid_days'])
            if used_count >= policy['max_use_count']:
                continue
        cap = min(balance, policy['max_use_per_order'])
        available += cap
    return available


def _sync_user_points(user_obj):
    """Userì˜ points_accumulated, points_event, points_cash í•©ì„ user.pointsì— ë°˜ì˜."""
    if not user_obj:
        return
    total = (getattr(user_obj, 'points_accumulated', 0) or 0) + (getattr(user_obj, 'points_event', 0) or 0) + (getattr(user_obj, 'points_cash', 0) or 0)
    user_obj.points = total


def _ensure_user_point_columns(user_obj):
    """ê¸°ì¡´ì— pointsë§Œ ìˆê³  3ì¢… ì»¬ëŸ¼ì´ 0ì¸ ê²½ìš°, pointsë¥¼ ì ë¦½í¬ì¸íŠ¸ë¡œ ê°„ì£¼í•´ ë™ê¸°í™”."""
    if not user_obj:
        return
    pa = getattr(user_obj, 'points_accumulated', 0) or 0
    pe = getattr(user_obj, 'points_event', 0) or 0
    pc = getattr(user_obj, 'points_cash', 0) or 0
    if (pa + pe + pc) == 0 and (getattr(user_obj, 'points', 0) or 0) > 0:
        user_obj.points_accumulated = user_obj.points
    _sync_user_points(user_obj)


def apply_order_points(user, order_total, points_used, order_id=None):
    """ê²°ì œ ì™„ë£Œ ì‹œ í¬ì¸íŠ¸ ì‚¬ìš©ë¶„ ì°¨ê°. ì‚¬ìš© ìš°ì„ ìˆœìœ„: ì´ë²¤íŠ¸ â†’ ìºì‹œì¶©ì „ â†’ ì ë¦½.
    í¬ì¸íŠ¸ ì‚¬ìš©ë¶„ì€ ë‹¹ì¼ ì£¼ë¬¸/ì°¨ê°ì—ì„œ ì°¨ê°í•œ ë’¤ ë³„ë„ ì •ì‚°ì—ì„œ ì²˜ë¦¬í•˜ë©°, MarketingCostì— ê¸°ë¡."""
    user_obj = user if hasattr(user, 'points') else User.query.get(user)
    if not user_obj or points_used <= 0:
        return
    _ensure_user_point_columns(user_obj)
    remaining = points_used
    # ì´ë²¤íŠ¸ â†’ ìºì‹œì¶©ì „ â†’ ì ë¦½ ìˆœìœ¼ë¡œ ì°¨ê°
    for col, ptype in [
        ('points_event', POINT_TYPE_EVENT),
        ('points_cash', POINT_TYPE_CASH),
        ('points_accumulated', POINT_TYPE_ACCUMULATED),
    ]:
        if remaining <= 0:
            break
        cur = getattr(user_obj, col, 0) or 0
        deduct = min(cur, remaining)
        if deduct > 0:
            setattr(user_obj, col, cur - deduct)
            db.session.add(PointLog(user_id=user_obj.id, amount=-deduct, point_type=ptype, order_id=order_id, memo='ì£¼ë¬¸ ì‚¬ìš©'))
            remaining -= deduct
    _sync_user_points(user_obj)
    db.session.add(MarketingCost(order_id=order_id, user_id=user_obj.id, amount=points_used, memo='ê³ ê° í¬ì¸íŠ¸ ì‚¬ìš© (ë³„ë„ ì •ì‚°)'))


def apply_points_on_delivery_complete(order_item):
    """í’ˆëª© ë°°ì†¡ì™„ë£Œ ì‹œ ì ë¦½. í•´ë‹¹ í’ˆëª©ì˜ ì •ì‚°ë²ˆí˜¸(sales_amount) ê¸°ì¤€ìœ¼ë¡œ ì ë¦½í¬ì¸íŠ¸ë§Œ ì ë¦½. 1íšŒë§Œ ì ë¦½."""
    if not order_item or getattr(order_item, 'cancelled', False):
        return
    order = Order.query.get(order_item.order_id) if order_item.order_id else None
    if not order or not order.user_id:
        return
    if PointLog.query.filter_by(order_item_id=order_item.id).filter(PointLog.amount > 0).first():
        return
    st = Settlement.query.filter_by(order_item_id=order_item.id).first()
    if not st or not getattr(st, 'sales_amount', 0):
        return
    rate, _, _ = _get_point_config()
    earned = int(st.sales_amount * rate / 1000) if rate else 0
    if earned <= 0:
        return
    u = User.query.get(order.user_id)
    if not u:
        return
    u.points_accumulated = (getattr(u, 'points_accumulated', 0) or 0) + earned
    _sync_user_points(u)
    db.session.add(PointLog(user_id=u.id, amount=earned, point_type=POINT_TYPE_ACCUMULATED, order_id=order.id, order_item_id=order_item.id, memo='ë°°ì†¡ì™„ë£Œ ì ë¦½'))


@login_manager.user_loader
def load_user(user_id):
    if not user_id:
        return None
    try:
        return User.query.get(int(user_id))
    except (TypeError, ValueError):
        return None

