from PIL import Image, ImageOps

ALLOWED_IMAGE_EXTENSIONS = (".jpg", ".jpeg", ".png", ".gif", ".webp")

def _is_allowed_image_filename(filename):
    """ì—…ë¡œë“œ íŒŒì¼ì´ í—ˆìš© ì´ë¯¸ì§€ í™•ì¥ìì¸ì§€ ê²€ì‚¬ (ë³´ì•ˆ: ì‹¤í–‰ íŒŒì¼ ë“± ì°¨ë‹¨)"""
    if not filename:
        return False
    ext = os.path.splitext(secure_filename(filename))[1].lower()
    return ext in ALLOWED_IMAGE_EXTENSIONS

def save_uploaded_file(file):
    """í•¸ë“œí° ì‚¬ì§„ ë¹„ìœ¨ ì •ë¦¬(ì¤‘ì•™ í¬ë¡­)Â·ì••ì¶•(WebP). Cloudinaryê°€ ì„¤ì •ë˜ì–´ ìˆìœ¼ë©´ Cloudinaryë¡œ ì—…ë¡œë“œ."""
    if not file or not getattr(file, 'filename', None):
        return None
    if getattr(file, 'stream', None) and hasattr(file.stream, 'seek'):
        try: file.stream.seek(0)
        except Exception: pass
    fn = (file.filename or '').strip()
    if not fn:
        fn = 'upload.jpg'
    if not _is_allowed_image_filename(fn):
        return None
    try:
        if getattr(file, 'stream', None) and hasattr(file.stream, 'seek'):
            try: file.stream.seek(0)
            except Exception: pass
        file_bytes = file.read()
        if not file_bytes:
            print("[save_uploaded_file] file.read() returned empty bytes")
            return None
        if cloudinary_url:
            upload_res = cloudinary.uploader.upload(
                file_bytes,
                folder="basket-uncle/main",
            )
            url = upload_res.get("secure_url") or upload_res.get("url")
            if not url:
                print(f"[save_uploaded_file] Cloudinary returned no URL: {upload_res}")
            elif "/upload/" in url:
                url = url.replace("/upload/", "/upload/w_800,h_800,c_fill/")
            return url
        img = Image.open(BytesIO(file_bytes))
        img = ImageOps.exif_transpose(img)
        size = (800, 800)
        img = ImageOps.fit(img, size, Image.Resampling.LANCZOS)
        new_filename = f"uncle_{datetime.now().strftime('%Y%m%d_%H%M%S_%f')}.webp"
        save_path = os.path.join(app.config['UPLOAD_FOLDER'], new_filename)
        img.save(save_path, "WEBP", quality=85)
        return f"/static/uploads/{new_filename}"
    except Exception as e:
        print(f"[save_uploaded_file] ERROR: {e}")
        import traceback; traceback.print_exc()
        return None


def save_detail_image(file):
    """ìƒì„¸ì´ë¯¸ì§€ ì—…ë¡œë“œ. Cloudinary ì‹œ ì›ë³¸ ì—…ë¡œë“œ í›„ URLì— w_1200,c_limit ë³€í™˜ ì ìš©. ë¡œì»¬ ì‹œ ê°€ë¡œ 1200px ë¦¬ì‚¬ì´ì¦ˆ."""
    if not file or not getattr(file, 'filename', None):
        return None
    if getattr(file, 'stream', None) and hasattr(file.stream, 'seek'):
        try: file.stream.seek(0)
        except Exception: pass
    fn = (file.filename or '').strip()
    if not fn:
        fn = 'upload.jpg'
    if not _is_allowed_image_filename(fn):
        return None
    try:
        if getattr(file, 'stream', None) and hasattr(file.stream, 'seek'):
            try: file.stream.seek(0)
            except Exception: pass
        file_bytes = file.read()
        if not file_bytes:
            return None
        if cloudinary_url:
            upload_res = cloudinary.uploader.upload(
                file_bytes,
                folder="basket-uncle/detail",
            )
            url = upload_res.get("secure_url") or upload_res.get("url")
            if url and "/upload/" in url:
                url = url.replace("/upload/", "/upload/w_1200,c_limit/")
            return url
        img = Image.open(BytesIO(file_bytes))
        img = ImageOps.exif_transpose(img)
        w, h = img.size
        if w > 1200:
            ratio = 1200 / w
            img = img.resize((1200, int(h * ratio)), Image.Resampling.LANCZOS)
        new_filename = f"detail_{datetime.now().strftime('%Y%m%d_%H%M%S_%f')}.webp"
        save_path = os.path.join(app.config['UPLOAD_FOLDER'], new_filename)
        img.save(save_path, "WEBP", quality=88)
        return f"/static/uploads/{new_filename}"
    except Exception as e:
        print(f"[save_detail_image] ERROR: {e}")
        return None


