@app.route('/order/payment', methods=['GET', 'POST'])
@login_required
def order_payment():
    """í† ìŠ¤í˜ì´ë¨¼ì¸  ê²°ì œì°½ í˜¸ì¶œ ë° ë³´ì•ˆ ê°•í™” ë²„ì „"""
    if request.method == 'POST':
        points_used = request.form.get('points_used', '0').strip()
        try:
            points_used = int(points_used) if points_used else 0
        except ValueError:
            points_used = 0
        quick_agree = request.form.get('quick_agree', '0').strip() in ('1', 'on', 'yes')
        order_address = _normalize_address_for_zone(request.form.get('order_address', '') or '')
        order_address_detail = request.form.get('order_address_detail', '').strip()
        order_entrance_pw = request.form.get('order_entrance_pw', '').strip()
        save_address_to_profile = request.form.get('save_address_to_profile', '0').strip() in ('1', 'on', 'yes')
        # ë§ˆì´í˜ì´ì§€ì™€ ë™ì¼: íšŒì› ì €ì¥ ì£¼ì†Œ ìš°ì„ , ê²°ì œ ì‹œ ì‚¬ìš© ì£¼ì†ŒëŠ” ë™ì¼í•œ ì •ê·œí™”Â·ê²€ì‚¬ ì ìš©
        user_addr = _normalize_address_for_zone(current_user.address or "")
        effective_address = order_address if order_address else user_addr
        items = Cart.query.filter_by(user_id=current_user.id).all()
        if not items:
            return redirect('/order/confirm')
        if not is_address_in_delivery_zone(effective_address):
            # í¼ ì£¼ì†Œê°€ ë°°ì†¡ë¶ˆê°€ì—¬ë„, íšŒì› ì£¼ì†Œ(ë§ˆì´í˜ì´ì§€ì™€ ë™ì¼)ë¡œ ì¬ê²€ì‚¬í•´ ë°°ì†¡ê°€ëŠ¥ì´ë©´ ì§„í–‰
            if user_addr and is_address_in_delivery_zone(user_addr):
                effective_address = user_addr
            else:
                flash("ì„ íƒí•œ ë°°ì†¡ì§€ëŠ” ë°°ì†¡ ê°€ëŠ¥ êµ¬ì—­ì´ ì•„ë‹™ë‹ˆë‹¤. ì£¼ì†Œë¥¼ í™•ì¸í•´ ì£¼ì„¸ìš”.")
                return redirect('/order/confirm')
        zone_type = get_delivery_zone_type(effective_address)
        if zone_type == 'quick' and not quick_agree:
            return redirect('/order/confirm')
        session['order_address'] = effective_address
        session['order_address_apt'] = (request.form.get('order_address_apt') or '').strip() or (getattr(current_user, 'address_apt_name', None) or '')
        session['order_address_detail'] = order_address_detail if order_address else (current_user.address_detail or "")
        session['order_entrance_pw'] = order_entrance_pw if order_address else (current_user.entrance_pw or "")
        session['save_address_to_profile'] = save_address_to_profile
        # ì§€ì˜¤ì½”ë”©ì€ ê²°ì œ ì™„ë£Œ ì‹œ(payment_success)ì— ìˆ˜í–‰. ì—¬ê¸°ì„œ í˜¸ì¶œ ì‹œ ì™¸ë¶€ API ëŒ€ê¸°ë¡œ ê²°ì œ í˜ì´ì§€ ì§„ì…ì´ ëŠë ¤ì§.
        session['delivery_lat'] = None
        session['delivery_lng'] = None
        subtotal = sum(i.price * i.quantity for i in items)
        cat_price_sums = {}
        for i in items:
            cat_price_sums[i.product_category] = cat_price_sums.get(i.product_category, 0) + (i.price * i.quantity)
        cat_item_counts = {}
        for i in items:
            cat_item_counts[i.product_category] = cat_item_counts.get(i.product_category, 0) + i.quantity
        delivery_fee, _ = calc_delivery_fee_for_categories(cat_price_sums, cat_item_counts)
        total = subtotal + delivery_fee
        quick_extra_fee_val = 0
        if zone_type == 'quick' and quick_agree:
            quick_extra_fee_val, _ = get_quick_extra_config()
            total += quick_extra_fee_val
        _, min_order_to_use, max_points_per_order = _get_point_config()
        user_points = getattr(current_user, 'points', 0) or 0
        max_allowed = _effective_max_point_use(current_user, total)
        if points_used < 0:
            points_used = 0
        if points_used > max_allowed:
            points_used = 0
        session['points_used'] = points_used
        session['quick_extra_fee'] = quick_extra_fee_val
        return redirect(url_for('order_payment'))
    items = Cart.query.filter_by(user_id=current_user.id).all()
    # ë§ˆì´í˜ì´ì§€ì™€ ë™ì¼í•˜ê²Œ: íšŒì› ì €ì¥ ì£¼ì†Œ ìš°ì„  ì‚¬ìš©(ì„¸ì…˜ì€ ì´ì „ ê²°ì œ ì‹œë„ ê°’ì´ë¼ ë§ˆì´í˜ì´ì§€ ìˆ˜ì • í›„ ë¶ˆì¼ì¹˜ ê°€ëŠ¥)
    effective_addr = _normalize_address_for_zone(current_user.address or "") or _normalize_address_for_zone(session.get('order_address') or "")
    if not items or not effective_addr or not is_address_in_delivery_zone(effective_addr):
        return redirect('/order/confirm')
    
    subtotal = sum(i.price * i.quantity for i in items)
    cat_price_sums = {}
