@login_required
def admin_point_adjust():
    """íšŒì› í¬ì¸íŠ¸ ì§€ê¸‰/ì°¨ê° (ë§ˆìŠ¤í„° ì „ìš©). user_id, amount(ì–‘ìˆ˜=ì§€ê¸‰/ìŒìˆ˜=ì°¨ê°), point_type(accumulated/event/cash), memo"""
    if not current_user.is_admin:
        return jsonify({'error': 'ê¶Œí•œ ì—†ìŒ'}), 403
    data = request.get_json() or request.form
    try:
        uid = int(data.get('user_id', 0))
        amount = int(data.get('amount', 0))
    except (TypeError, ValueError):
        return jsonify({'error': 'user_id, amount í•„ìš”'}), 400
    point_type = (data.get('point_type') or 'accumulated').strip().lower()
    if point_type not in (POINT_TYPE_ACCUMULATED, POINT_TYPE_EVENT, POINT_TYPE_CASH):
        point_type = POINT_TYPE_ACCUMULATED
    u = User.query.get(uid)
    if not u:
        return jsonify({'error': 'íšŒì› ì—†ìŒ'}), 404
    memo = (data.get('memo') or '')[:200]
    _ensure_user_point_columns(u)
    col = {'accumulated': 'points_accumulated', 'event': 'points_event', 'cash': 'points_cash'}[point_type]
    current_col = getattr(u, col, 0) or 0
    after_col = current_col + amount
    if after_col < 0:
        return jsonify({'error': 'í•´ë‹¹ ìœ í˜• í¬ì¸íŠ¸ê°€ ìŒìˆ˜ê°€ ë  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.'}), 400
    setattr(u, col, after_col)
    _sync_user_points(u)
    db.session.add(PointLog(user_id=u.id, amount=amount, point_type=point_type, memo=memo or ('ê´€ë¦¬ì ì¡°ì •' if amount >= 0 else 'ê´€ë¦¬ì ì°¨ê°'), adjusted_by=current_user.id))
    db.session.commit()
    return jsonify({'ok': True, 'after': u.points, 'after_accumulated': u.points_accumulated or 0, 'after_event': u.points_event or 0, 'after_cash': u.points_cash or 0})


@login_required
def admin_point_log():
    """íšŒì›ë³„ í¬ì¸íŠ¸ ë‚´ì—­ (ë§ˆìŠ¤í„° ì „ìš©). user_id, limit ê¸°ë³¸ 30"""
    if not current_user.is_admin:
        return jsonify({'error': 'ê¶Œí•œ ì—†ìŒ'}), 403
    uid = request.args.get('user_id', type=int)
    limit = min(100, max(1, request.args.get('limit', type=int) or 30))
    if not uid:
        return jsonify({'error': 'user_id í•„ìš”'}), 400
    logs = PointLog.query.filter_by(user_id=uid).order_by(PointLog.created_at.desc()).limit(limit).all()
    out = []
    for l in logs:
        modifier = None
        if getattr(l, 'adjusted_by', None):
            mod_user = User.query.get(l.adjusted_by)
            modifier = mod_user.email if mod_user else str(l.adjusted_by)
        out.append({
            'id': l.id, 'amount': l.amount, 'order_id': l.order_id, 'memo': l.memo or '',
            'point_type': getattr(l, 'point_type', None) or 'accumulated',
            'created_at': l.created_at.strftime('%Y-%m-%d %H:%M') if l.created_at else '',
            'date': l.created_at.strftime('%Y-%m-%d') if l.created_at else '',
            'modifier': modifier
        })
    return jsonify({'logs': out})


