                    if detail_imgs_raw and not _bulk_is_placeholder_image(detail_imgs_raw):
                        parts = [p.strip().lstrip('/').strip('"').strip("'") for p in detail_imgs_raw.split(',') if p.strip() and not _bulk_is_placeholder_image(p.strip())]
