def _bulk_is_placeholder_image(s):
    """ì—‘ì…€ ì–‘ì‹ placeholder(íŒŒì¼ëª….jpg ë“±)ì´ë©´ True. ì‹¤ì œ íŒŒì¼ëª…ë§Œ URLì— ì“°ê¸° ìœ„í•´."""
    if not s or not isinstance(s, str):
        return True
    t = s.strip().lower()
    if not t:
        return True
    if t.startswith('(') and ')' in t:
        return True
    if 'íŒŒì¼ëª…' in t or 'file' in t and 'example' in t:
        return True
    if t in ('(íŒŒì¼ëª….jpg)', '(íŒŒì¼ëª…)', 'íŒŒì¼ëª….jpg', 'íŒŒì¼ëª…', '(a.jpg,b.jpg)', '(ì˜ˆ: apple.jpg)'):
        return True
    return False


def _bulk_image_filename_only(s):
    """ì—‘ì…€ì— ì „ì²´ ê²½ë¡œë‚˜ ë”°ì˜´í‘œê°€ ë“¤ì–´ì™€ë„ URLì—ëŠ” íŒŒì¼ëª…ë§Œ ì‚¬ìš©. ì˜ˆ: \"C:/Users/.../1.jpg\" -> 1.jpg"""
    if not s or not isinstance(s, str):
        return ""
    t = s.strip().strip('"').strip("'").strip()
    if not t:
        return ""
    if '/' in t or '\\' in t:
        return os.path.basename(t)
    return t


def _bulk_try_copy_from_absolute_path(raw_value, upload_dir):
    """ì—‘ì…€ ì…€ì— ë¡œì»¬ ì „ì²´ ê²½ë¡œê°€ ìˆìœ¼ë©´ Cloudinary ë˜ëŠ” upload_dirì— ë³µì‚¬í•˜ê³  URL ë°˜í™˜. ì—†ê±°ë‚˜ ì‹¤íŒ¨ ì‹œ None."""
    if not raw_value or not isinstance(raw_value, str):
        return None
    import shutil
    path = raw_value.strip().strip('"').strip("'").strip()
    if not path:
        return None
    is_absolute = (len(path) >= 2 and path[1] == ':') or path.startswith('/') or path.startswith('\\') or ('\\' in path and path[0].isalpha())
    if not is_absolute or not os.path.isfile(path):
        return None
    try:
        if cloudinary_url:
            with open(path, 'rb') as fh:
                file_bytes = fh.read()
            res = cloudinary.uploader.upload(file_bytes, folder="basket-uncle/main")
            url = res.get("secure_url") or res.get("url")
            if url and "/upload/" in url:
                url = url.replace("/upload/", "/upload/w_800,h_800,c_fill/")
            return url
        ext = (os.path.splitext(path)[1] or '.jpg').lower()
        if ext not in ('.jpg', '.jpeg', '.png', '.webp', '.gif', '.bmp'):
            ext = '.jpg'
        new_name = f"bulk_{datetime.now().strftime('%Y%m%d_%H%M%S')}_{uuid.uuid4().hex[:8]}{ext}"
        dest = os.path.join(os.path.abspath(upload_dir), new_name)
        shutil.copy2(path, dest)
        if os.path.isfile(dest):
            return f"/static/uploads/{new_name}"
    except Exception as e:
        print(f"[_bulk_try_copy_from_absolute_path] ERROR: {e}")
    return None


def _bulk_copy_detail_2_to_10_from_same_folder(main_absolute_path, upload_dir):
    """ëŒ€í‘œì´ë¯¸ì§€ ì „ì²´ ê²½ë¡œ(ì˜ˆ: C:\\...\\1.jpg) ê¸°ì¤€ìœ¼ë¡œ ê°™ì€ í´ë”ì˜ 2.jpg~10.jpgë¥¼ Cloudinary ë˜ëŠ” ë¡œì»¬ì— ë³µì‚¬."""
    if not main_absolute_path or not isinstance(main_absolute_path, str):
        return []
    import shutil
    path = main_absolute_path.strip().strip('"').strip("'").strip()
    if not path or not os.path.isfile(path):
        return []
    dir_path = os.path.dirname(path)
    upload_abs = os.path.abspath(upload_dir)
    os.makedirs(upload_abs, exist_ok=True)
    exts = ('.jpg', '.jpeg', '.png', '.webp', '.gif', '.bmp')
    urls = []
    for num in range(2, 11):
        candidate = None
        for e in exts:
            p = os.path.join(dir_path, str(num) + e)
            if os.path.isfile(p):
                candidate = p
                break
        if not candidate:
            continue
        try:
            if cloudinary_url:
                with open(candidate, 'rb') as fh:
                    file_bytes = fh.read()
                res = cloudinary.uploader.upload(file_bytes, folder="basket-uncle/detail")
                url = res.get("secure_url") or res.get("url")
                if url:
                    if "/upload/" in url:
                        url = url.replace("/upload/", "/upload/w_1200,c_limit/")
                    urls.append(url)
            else:
                ext = os.path.splitext(candidate)[1].lower()
                new_name = f"bulk_{num}_{datetime.now().strftime('%Y%m%d_%H%M%S')}_{uuid.uuid4().hex[:8]}{ext}"
                dest = os.path.join(upload_abs, new_name)
                shutil.copy2(candidate, dest)
                if os.path.isfile(dest):
                    urls.append(f"/static/uploads/{new_name}")
        except Exception as e:
            print(f"[_bulk_copy_detail] num={num} ERROR: {e}")
    return urls


def _bulk_collect_images_from_folder(images_root, product_name, upload_dir):
    """images_root(ë° í•˜ìœ„)ì—ì„œ ìƒí’ˆëª…ê³¼ ë™ì¼í•œ í´ë”ë¥¼ ì°¾ì•„ 1~10ë²ˆ ì´ë¯¸ì§€ ìˆ˜ì§‘. 1=ëŒ€í‘œ, 2~10=ìƒì„¸. ë³µì‚¬ í›„ URL ë°˜í™˜."""
    import shutil
    import unicodedata
    import re
    IMAGE_EXT = ('.jpg', '.jpeg', '.png', '.webp', '.gif', '.bmp')
    main_url = ""
    detail_urls = []

    def norm(s):
        return unicodedata.normalize('NFC', (s or '').strip())

    def norm_for_match(s):
        """ë¹„êµìš©: NFC ì •ê·œí™” í›„ ì—°ì† ê³µë°±(ì „ê° í¬í•¨)ì„ í•˜ë‚˜ì˜ ê³µë°±ìœ¼ë¡œ."""
        t = unicodedata.normalize('NFC', (s or '').strip())
        t = re.sub(r'[\s\u3000]+', ' ', t)  # ê³µë°±Â·ì „ê°ê³µë°± ë“± ì—°ì†ì„ í•˜ë‚˜ë¡œ
        return t.strip()

    product_name_norm = norm(product_name)
    product_name_match = norm_for_match(product_name)
    if not product_name_norm:
        return main_url, detail_urls

    product_folder = None
    candidate = os.path.join(images_root, product_name.strip())
    if os.path.isdir(candidate):
        product_folder = candidate
    if not product_folder:
        try:
            for d in os.listdir(images_root):
                if not os.path.isdir(os.path.join(images_root, d)):
                    continue
                dn = norm(d)
                dm = norm_for_match(d)
                if dn == product_name_norm or dm == product_name_match or d.strip() == product_name.strip():
                    product_folder = os.path.join(images_root, d)
                    break
        except Exception:
            pass
    if not product_folder:
        try:
            for root, dirs, _ in os.walk(images_root):
                for d in dirs:
                    dn = norm(d)
                    dm = norm_for_match(d)
                    if dn == product_name_norm or dm == product_name_match or d.strip() == product_name.strip():
                        product_folder = os.path.join(root, d)
                        break
                if product_folder:
                    break
        except Exception:
            pass

    if not product_folder or not os.path.isdir(product_folder):
        return main_url, detail_urls

    by_num = {}
    try:
        for f in os.listdir(product_folder):
            base, ext = os.path.splitext(f)
            if ext.lower() not in IMAGE_EXT:
                continue
            try:
                n = int(base.strip())
                if 1 <= n <= 10:
                    by_num[n] = os.path.join(product_folder, f)
            except ValueError:
                continue
    except Exception:
        return main_url, detail_urls

    upload_dir_abs = os.path.abspath(upload_dir)
    os.makedirs(upload_dir_abs, exist_ok=True)
    ts = datetime.now().strftime('%Y%m%d_%H%M%S')
    for num in range(1, 11):
        src = by_num.get(num)
        if not src or not os.path.isfile(src):
            continue
        ext = os.path.splitext(src)[1].lower()
        try:
            if cloudinary_url:
                with open(src, 'rb') as fh:
                    file_bytes = fh.read()
                if num == 1:
                    res = cloudinary.uploader.upload(file_bytes, folder="basket-uncle/main")
                    url = res.get("secure_url") or res.get("url") or ""
                    if url and "/upload/" in url:
                        url = url.replace("/upload/", "/upload/w_800,h_800,c_fill/")
                else:
                    res = cloudinary.uploader.upload(file_bytes, folder="basket-uncle/detail")
                    url = res.get("secure_url") or res.get("url") or ""
                    if url and "/upload/" in url:
                        url = url.replace("/upload/", "/upload/w_1200,c_limit/")
            else:
                new_name = f"bulk_{num}_{ts}_{uuid.uuid4().hex[:8]}{ext}"
                dest = os.path.join(upload_dir_abs, new_name)
                shutil.copy2(src, dest)
                if not os.path.isfile(dest):
                    continue
                url = f"/static/uploads/{new_name}"
            if url:
                if num == 1:
                    main_url = url
                else:
                    detail_urls.append(url)
        except Exception as e:
            print(f"[_bulk_collect_images_from_folder] num={num} ERROR: {e}")
            continue
    if not detail_urls and main_url:
        detail_urls = [main_url]
    return main_url, detail_urls


def _cell_str(v):
    if v is None or (isinstance(v, float) and pd.isna(v)):
        return ""
    s = str(v).strip()
    return s if s and s.lower() not in ('nan', 'none') else ""


def _cell_int(v):
    if v is None or (isinstance(v, float) and pd.isna(v)):
        return None
    try:
        return int(float(v))
    except (ValueError, TypeError):
        return None

