@app.route('/order_view/<code>/send', methods=['POST'])
def order_view_send(code):
    """ê³µìœ  ë°œì£¼ì„œ í˜ì´ì§€ì—ì„œ ì´ë©”ì¼ ì¼ê´„ ë°œì†¡. ì„¸ì…˜ ì¸ì¦ í•„ìš”."""
    if not _order_view_verified(code):
        return jsonify({"success": False, "message": "ë¹„ë°€ë²ˆí˜¸ë¥¼ ë¨¼ì € ì…ë ¥í•´ ì£¼ì„¸ìš”."}), 403
    link = OrderViewLink.query.filter_by(code=code).first()
    if not link:
        return jsonify({"success": False, "message": "ë§í¬ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤."}), 404
    data = request.get_json() or request.form
    subject = (data.get('subject') or '').strip()
    body = (data.get('body') or '').strip()
    items = data.get('items') or []
    if not subject:
        return jsonify({"success": False, "message": "ì œëª©ì„ ì…ë ¥í•´ ì£¼ì„¸ìš”."}), 400
    if not isinstance(items, list):
        items = []
    sent = 0
    for it in items:
        to = (it.get('to_email') or '').strip() if isinstance(it, dict) else ''
        if not to or '@' not in to:
            continue
        try:
            send_mail(to, subject, body)
            sent += 1
        except Exception:
            pass
    return jsonify({"success": True, "sent_count": sent, "message": "ì´ë©”ì¼ì„ ë°œì†¡í–ˆìŠµë‹ˆë‹¤."})


