@login_required
def admin_backup_run():
    """ê´€ë¦¬ì: ìˆ˜ë™ ë°±ì—… ì‹¤í–‰ (SQLite â†’ zip â†’ GitHub Release ë˜ëŠ” ë¡œì»¬)."""
    if not current_user.is_admin:
        return jsonify({"success": False, "message": "ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤."}), 403
    ok, msg = run_backup()
