@login_required
def admin_delete(pid):
    """ìƒí’ˆ ì‚­ì œ"""
    p = Product.query.get(pid)
    if p and check_admin_permission(p.category): db.session.delete(p); db.session.commit()
    return redirect('/admin')

@login_required
def admin_product_end_sale(pid):
    """ìƒí’ˆ íŒë§¤ì¢…ë£Œ (is_active=False). ëª©ë¡Â·ìƒì„¸ì—ì„œ ë…¸ì¶œë˜ì§€ ì•ŠìŒ."""
    p = Product.query.get_or_404(pid)
    if not check_admin_permission(p.category):
        flash("í•´ë‹¹ ì¹´í…Œê³ ë¦¬ ìƒí’ˆì— ëŒ€í•œ ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤.")
        return redirect('/admin')
    p.is_active = False
    db.session.commit()
    flash(f"ã€Œ{p.name}ã€ íŒë§¤ë¥¼ ì¢…ë£Œí–ˆìŠµë‹ˆë‹¤.")
    _next = request.args.get('next') or request.referrer
