@login_required
def admin_orders_sales_excel():
    """ì¡°íšŒ ê²°ê³¼ ìƒì„¸ í…Œì´ë¸” ì—‘ì…€ ë‹¤ìš´ë¡œë“œ (ì£¼ë¬¸ì¼ì‹œ, íŒë§¤ìƒí’ˆëª…, íŒë§¤ìˆ˜ëŸ‰, ê²°ì œìƒíƒœ)"""
    categories = Category.query.all()
    my_categories = [c.name for c in categories if c.manager_email == current_user.email]
    if not (current_user.is_admin or my_categories):
        flash("ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤.")
        return redirect('/admin')
    is_master = current_user.is_admin
    now = datetime.now()
    start_date_str = request.args.get('start_date', now.strftime('%Y-%m-%d 00:00')).replace('T', ' ')
    end_date_str = request.args.get('end_date', now.strftime('%Y-%m-%d 23:59')).replace('T', ' ')
    query = Order.query.filter(Order.status != 'ê²°ì œì·¨ì†Œ')
    order_ids_param = request.args.get('order_ids', '').strip()
    if order_ids_param:
        allowed_ids = [x.strip() for x in order_ids_param.split(',') if x.strip()]
        if allowed_ids:
            query = query.filter(Order.order_id.in_(allowed_ids))
    else:
        try:
            sd = datetime.strptime(start_date_str, '%Y-%m-%d %H:%M')
            ed = datetime.strptime(end_date_str, '%Y-%m-%d %H:%M')
            query = query.filter(Order.created_at >= sd, Order.created_at <= ed)
        except Exception:
            pass
    sel_order_cat = request.args.get('order_cat', 'ì „ì²´')
    orders = query.order_by(Order.created_at.desc()).all()
    sales_table_rows = []
    for o in orders:
        if is_master:
            order_show = True
        else:
            order_show = False
            parts = (o.product_details or '').split(' | ')
            for part in parts:
                match = re.search(r'\[(.*?)\] (.*)', part)
                if match and match.group(1).strip() in my_categories:
                    order_show = True
                    break
        if not order_show:
            continue
        parts = (o.product_details or '').split(' | ')
        order_date_str = o.created_at.strftime('%Y-%m-%d %H:%M') if o.created_at else ''
        status_str = o.status or 'ê²°ì œì™„ë£Œ'
        items = OrderItem.query.filter_by(order_id=o.id).order_by(OrderItem.id.asc()).all()
        if items:
            for oi in items:
                if (is_master or oi.product_category in my_categories) and (sel_order_cat == 'ì „ì²´' or oi.product_category == sel_order_cat):
                    is_cancelled = getattr(oi, 'cancelled', False) or (getattr(oi, 'item_status', None) in ('ë¶€ë¶„ì·¨ì†Œ', 'í’ˆì ˆì·¨ì†Œ'))
                    sales_table_rows.append({
                        'order_date': order_date_str,
                        'order_id': getattr(o, 'order_id', None) or '',
                        'product_name': oi.product_name,
                        'category': oi.product_category,
                        'quantity': 0 if is_cancelled else oi.quantity,
                        'status': 'ì·¨ì†Œ' if is_cancelled else (getattr(oi, 'item_status', None) or status_str)
                    })
        else:
            for part in parts:
                match = re.search(r'\[(.*?)\] (.*)', part)
                if match:
                    cat_n, items_str = match.groups()
                    if (is_master or cat_n in my_categories) and (sel_order_cat == 'ì „ì²´' or cat_n == sel_order_cat):
                        for item in items_str.split(', '):
                            it_match = re.search(r'(.*?)\((\d+)\)', item)
                            if it_match:
                                pn, qt = it_match.groups()
                                sales_table_rows.append({'order_date': order_date_str, 'order_id': getattr(o, 'order_id', None) or '', 'product_name': pn.strip(), 'category': cat_n, 'quantity': int(qt), 'status': status_str})
    if not sales_table_rows:
        flash("ë‹¤ìš´ë¡œë“œí•  ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.")
        return redirect('/admin?tab=orders')
    df = pd.DataFrame(sales_table_rows, columns=['order_date', 'order_id', 'product_name', 'category', 'quantity', 'status'])
    df.columns = ['ì£¼ë¬¸ì¼ì‹œ', 'ì˜¤ë”ì•„ì´ë””', 'íŒë§¤ìƒí’ˆëª…', 'ì¹´í…Œê³ ë¦¬', 'íŒë§¤ìˆ˜ëŸ‰', 'ê²°ì œìƒíƒœ']
    out = BytesIO()
    with pd.ExcelWriter(out, engine='openpyxl') as w:
        df.to_excel(w, index=False)
    out.seek(0)
    filename = f"ì¡°íšŒê²°ê³¼ìƒì„¸_{datetime.now().strftime('%Y%m%d_%H%M%S')}.xlsx"
    resp = send_file(out, mimetype='application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', download_name=filename, as_attachment=True)
    resp.headers["Content-Disposition"] = f"attachment; filename*=UTF-8''{quote(filename)}"
    return resp


def _pil_font_for_table(size=12):
    """PIL í•œê¸€ ì§€ì› í°íŠ¸ ë°˜í™˜ (í…Œì´ë¸” ì´ë¯¸ì§€ìš©). ì„œë²„ í™˜ê²½ë³„ ê²½ë¡œ ì‹œë„ í›„ (font_body, font_header) ë°˜í™˜."""
    from PIL import ImageFont
    root = os.path.dirname(os.path.abspath(__file__))
    font_paths = [
        # Windows
        os.path.join(os.environ.get('WINDIR', ''), 'Fonts', 'malgun.ttf'),
        os.path.join(os.environ.get('WINDIR', ''), 'Fonts', 'gulim.ttc'),
        os.path.join(os.environ.get('WINDIR', ''), 'Fonts', 'batang.ttc'),
        os.path.join(os.environ.get('WINDIR', ''), 'Fonts', 'dotum.ttf'),
        # í”„ë¡œì íŠ¸ ë‚´ í°íŠ¸ (ë°°í¬ ì‹œ static/fonts/ ì— í•œê¸€ ttf ì¶”ê°€ ê¶Œì¥)
        os.path.join(root, 'static', 'fonts', 'NanumGothic.ttf'),
        os.path.join(root, 'static', 'fonts', 'malgun.ttf'),
        os.path.join(root, 'NanumGothic.ttf'),
        os.path.join(root, 'malgun.ttf'),
        'malgun.ttf',
        'NanumGothic.ttf',
        # Linux (ë‚˜ëˆ”/ë…¸í†  ë“±)
        '/usr/share/fonts/truetype/nanum/NanumGothic.ttf',
        '/usr/share/fonts/truetype/nanum/NanumBarunGothic.ttf',
        '/usr/share/fonts/truetype/fonts-nanum/NanumGothic.ttf',
        '/usr/share/fonts/truetype/fonts-nanum/NanumBarunGothic.ttf',
        '/usr/share/fonts/opentype/noto/NotoSansCJK-Regular.ttc',
        '/usr/share/fonts/truetype/noto/NotoSansCJK-Regular.ttc',
        '/usr/share/fonts/opentype/noto-cjk/NotoSansCJK-Regular.ttc',
        '/usr/share/fonts/truetype/liberation/LiberationSans-Regular.ttf',
        '/usr/share/fonts/truetype/dejavu/DejaVuSans.ttf',
        # macOS
        '/System/Library/Fonts/Supplemental/AppleGothic.ttf',
        '/Library/Fonts/AppleGothic.ttf',
        os.path.expanduser('~/Library/Fonts/NanumGothic.ttf'),
    ]
    for path in font_paths:
        if path and os.path.isfile(path):
            try:
                f = ImageFont.truetype(path, size)
                return (f, f)
            except Exception:
                continue
    f = ImageFont.load_default()
    return (f, f)


def _pil_table_col_widths(headers, row_cells_list, font_header, font, padding=24, min_w=50, max_w=900):
    """í—¤ë”+í–‰ ì…€ í…ìŠ¤íŠ¸ ê¸°ì¤€ìœ¼ë¡œ ì—´ ë„ˆë¹„(í”½ì…€) ê³„ì‚°. paddingë§Œí¼ ì—¬ë°±, min_w~max_w ë²”ìœ„. í•œê¸€ì€ ê¸€ìë‹¹ ì•½ 14px í´ë°±."""
    n = len(headers)
    col_w = []
    for j in range(n):
        try:
            w = font_header.getlength(headers[j])
        except Exception:
            w = 0
        if w <= 0:
            w = len(headers[j]) * 14
        for row in row_cells_list:
            if j < len(row):
                s = str(row[j])
                try:
                    w2 = font.getlength(s)
                except Exception:
                    w2 = 0
                if w2 <= 0:
                    w2 = len(s) * 14
                w = max(w, w2)
        w = max(min_w, min(max_w, int(w) + padding))
        col_w.append(w)
    return tuple(col_w)


