def _delivery_summary_rows(is_master, my_categories, sel_order_cat, start_dt, end_dt, order_ids_param, full_items=True):
    """ë°°ì†¡ì§‘ê³„ìš© í–‰ ëª©ë¡ ìƒì„±. full_items=Trueë©´ í’ˆëª© ì „ì²´, Falseë©´ ì´ë¯¸ì§€ìš© 60ì ì ˆë‹¨."""
    q_orders = Order.query.filter(Order.created_at >= start_dt, Order.created_at <= end_dt, Order.status != 'ê²°ì œì·¨ì†Œ')
    if order_ids_param:
        allowed_ids = [x.strip() for x in order_ids_param.split(',') if x.strip()]
        if allowed_ids:
            q_orders = q_orders.filter(Order.order_id.in_(allowed_ids))
    all_orders = q_orders.order_by(Order.created_at.desc()).all()
    delivery_rows = []
    for o in all_orders:
        order_show = False
        manager_items_list = []
        items = OrderItem.query.filter_by(order_id=o.id, cancelled=False).order_by(OrderItem.id.asc()).all()
        if items:
            for oi in items:
                if is_master or oi.product_category in my_categories:
                    order_show = True
                    if sel_order_cat == 'ì „ì²´' or oi.product_category == sel_order_cat:
                        manager_items_list.append(f"{oi.product_name}({oi.quantity})")
        else:
            parts = (o.product_details or '').split(' | ')
            for part in parts:
                match = re.search(r'\[(.*?)\] (.*)', part)
                if match:
                    cat_n = match.group(1).strip()
                    items_str = match.group(2).strip()
                    if is_master or cat_n in my_categories:
                        order_show = True
                        if sel_order_cat == 'ì „ì²´' or cat_n == sel_order_cat:
                            for item in items_str.split(', '):
                                it_match = re.search(r'(.*?)\((\d+)\)', item)
                                if it_match:
                                    pn, qt = it_match.groups()
                                    manager_items_list.append(f"{pn.strip()}({qt})")
        if order_show:
            items_str = ', '.join(manager_items_list)
            if not full_items:
                items_str = items_str[:90] + ('..' if len(items_str) > 90 else '')
            delivery_rows.append({
                'order_id': o.order_id or '',
                'order_date': o.created_at.strftime('%Y-%m-%d %H:%M') if o.created_at else '',
                'status': o.status or 'ê²°ì œì™„ë£Œ',
                'customer': f"{o.customer_name or ''} {o.customer_phone or ''}".strip(),
                'address': o.delivery_address or '',
                'items': items_str,
            })
    return delivery_rows


